stateMachine.definition:
{
  "Comment": "An example of the Amazon States Language for notification on an AWS Fargate task completion",
  "StartAt": "Run Fargate Task",
  "TimeoutSeconds": 3600,
  "QueryLanguage": "JSONata",
  "States": {
    "Run Fargate Task": {
      "Type": "Task",
      "Resource": "arn:aws:states:::ecs:runTask.sync",
      "Arguments": {
        "LaunchType": "FARGATE",
        "Cluster": "arn:aws:ecs:us-east-1:582739605646:cluster/StepFunctionsSample-ContainerTaskManagement-ed5da51d-3c1a-4e5d-bcdc-decfa8b4ee53-ECSCluster-JAx0FbQC8SgG",
        "TaskDefinition": "arn:aws:ecs:us-east-1:582739605646:task-definition/StepFunctionsSample-ContainerTaskManagement-ed5da51d-3c1a-4e5d-bcdc-decfa8b4ee53-ECSTaskDefinition-eHCFlna6FDfo:1",
        "NetworkConfiguration": {
          "AwsvpcConfiguration": {
            "Subnets": [
              "subnet-0be1e465e1365774b",
              "subnet-06e1bf9782e26377a"
            ],
            "AssignPublicIp": "ENABLED"
          }
        }
      },
      "Next": "Notify Success",
      "Catch": [
          {
            "ErrorEquals": [ "States.ALL" ],
            "Next": "Notify Failure"
          }
      ]
    },
    "Notify Success": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sns:publish",
      "Arguments": {
        "Message": "AWS Fargate Task started by Step Functions succeeded",
        "TopicArn": "arn:aws:sns:us-east-1:582739605646:StepFunctionsSample-ContainerTaskManagement-ed5da51d-3c1a-4e5d-bcdc-decfa8b4ee53-SNSTopic-E4wo4bkEcEBN"
      },
      "End": true
    },
    "Notify Failure": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sns:publish",
      "Arguments": {
        "Message": "AWS Fargate Task started by Step Functions failed",
        "TopicArn": "arn:aws:sns:us-east-1:582739605646:StepFunctionsSample-ContainerTaskManagement-ed5da51d-3c1a-4e5d-bcdc-decfa8b4ee53-SNSTopic-E4wo4bkEcEBN"
      },
      "End": true
    }
  }
}
